function populateChroniclesSelect() {
  const chronicleSelect = document.getElementById('chronicleSelect');
  
  for (let i = 1; i <= 50; i++) {
    const option = document.createElement('option');
    option.value = `crónica${i}`;
    option.textContent = `Crónica #${i}`;
    chronicleSelect.appendChild(option);
  }
}

// Función para actualizar la lista de palabras basada en la crónica seleccionada
function updateWordList() {
  const chronicleSelect = document.getElementById('chronicleSelect');
  const wordSelect = document.getElementById('wordSelect');
  const selectedChronicleKey = chronicleSelect.value;

  wordSelect.innerHTML = ''; // Limpiamos las opciones existentes

  if (chronicles[selectedChronicleKey]) {
    const words = chronicles[selectedChronicleKey].words;
    chronicles[selectedChronicleKey].wordCount = words.length; // Actualizamos el conteo de palabras

    words.forEach((word, index) => {
      const option = document.createElement('option');
      option.value = word;
      option.textContent = `${word} (Palabra #${index + 1})`;
      wordSelect.appendChild(option);
    });
  }
}

// Función para verificar la palabra seleccionada contra la entrada del usuario
function verifyWord() {
  const wordSelect = document.getElementById('wordSelect');
  const selectedWord = wordSelect.value;
  const userInput = document.getElementById('userInput').value;
  const resultContainer = document.getElementById('resultContainer');

  // Comprobación básica de coincidencia exacta
  if (userInput === selectedWord) {
    resultContainer.innerHTML = `<span class="correct">¡Correcto! La palabra "${selectedWord}" ha sido bien deletreada.</span>`;
    return;
  }

  // Comprobación de mayúsculas/minúsculas
  if (userInput.toLowerCase() === selectedWord.toLowerCase()) {
    resultContainer.innerHTML = `<span class="incorrect">Casi correcto, pero ha habido un error en el uso de mayúsculas y minúsculas.</span>`;
    return;
  }

  // Comprobación de tildes y caracteres especiales
  if (normalizeString(userInput) === normalizeString(selectedWord)) {
    resultContainer.innerHTML = `<span class="incorrect">Casi correcto, pero ha habido un error en la utilización de las tildes.</span>`;
    return;
  }

  // Comprobación de letras mal ubicadas o errores de escritura más generales
  resultContainer.innerHTML = `<span class="incorrect">Incorrecto. Ha habido un error en la ubicación de las letras, has indicado una letra que no corresponde a la secuencia.</span>`;
}

// Función auxiliar para normalizar strings (quitar tildes y caracteres especiales)
function normalizeString(str) {
  return str.normalize("NFD").replace(/[\u0300-\u036f]/g, "").toLowerCase();
}


// Inicialización de la página
window.onload = function() {
  populateChroniclesSelect();
  updateWordList();
};

